// Precondition: The array list has room for another entry.public void add(int newPosition, T newEntry){   checkInitialization();   if ((newPosition >= 1) && (newPosition <= numberOfEntries + 1))   {      if (newPosition <= numberOfEntries)         makeRoom(newPosition);      list[newPosition] = newEntry;      numberOfEntries++;      ensureCapacity(); // Ensure enough room for next add   }   else      throw new IndexOutOfBoundsException(                "Given position of add's new entry is out of bounds.");} // end add// Version 4.0